mapper.select:
SELECT a.*, b.c1Name, c.c2Name FROM `km_product` AS a JOIN `km_product_cate1` AS b ON a.prodCate1 = b.cate1 JOIN `km_product_cate2` AS c ON b.cate1 = c.cate1 AND a.prodCate2=c.cate2 WHERE a.prodCate1=#{cate2} and a.prodCate2=#{cate2}
order by `price` esc
order by #{sort} desc

SELECT a.*, b.prodName FROM `km_product_review` AS a
JOIN `km_product` AS b ON a.`prodNo` = b.`prodNo`
WHERE b.`prodNo` = ${prodNo}
order by `rdate` desc;
